mapper.select:
select count(id) from user


        and create_time > #{begin}


        and create_time < #{end}

mapper.update:
update user
username = #{username}, password_hash = #{passwordHash}, email = #{email}, mobile = #{mobile}, nickname = #{nickname}, avatar_url = #{avatarUrl}, role = #{role}, role_level = #{roleLevel}, status = #{status}, last_login_at = #{lastLoginAt}, last_login_ip = #{lastLoginIp}, extra = #{extra}, remark = #{remark}, created_at = #{createdAt}, updated_at = #{updatedAt}, deleted_at = #{deletedAt},
where id = #{id}
